mapper.select:
  	SELECT rs.UU_ID, rs.ACCOUNT_ID, rs.REVIEW_ID, rs.STORE_ID, rs.PATH, rs.ORIGINNAME, r.WRITEDATE 
	FROM REVIEWIMAGE rs JOIN REVIEW r ON rs.REVIEW_ID = r.REVIEW_ID
	WHERE rs.STORE_ID=#{STORE_ID} order by r.WRITEDATE DESC limit #{LIMIT}

mapper.update:
UPDATE STORE SET tasteLevel=#{tasteLevel}, priceLevel=#{priceLevel}, serviceLevel=#{serviceLevel}, 
moodLevel=#{moodLevel}, convenienceLevel=#{convenienceLevel}, averageLevel=#{averageLevel}
WHERE store_id=#{store_Id}

mapper.select:
SELECT STORE_ID, NAME, STOREIMG1, STOREIMG2, STOREIMG3, ROADADDRESS, VIEWCNT, AVERAGELEVEL, REVIEWCNT FROM (SELECT STORE_ID, NAME, STOREIMG1, STOREIMG2, STOREIMG3, ROADADDRESS, VIEWCNT, AVERAGELEVEL, REVIEWCNT, @ROWNUM:=@ROWNUM+1 AS ROWNUM, CNT FROM (SELECT STORE.STORE_ID, NAME, STOREIMG1, STOREIMG2, STOREIMG3, ROADADDRESS, STORE.VIEWCNT, AVERAGELEVEL, REVIEWCNT, LOGTABLE.VIEWCNT AS CNT, ST_Distance_Sphere(POINT(XLOCATION, YLOCATION), POINT(#{x}, #{y})) as DISTANCE FROM ((SELECT STORE_ID, COUNT(*) AS VIEWCNT FROM LOG, (SELECT @ROWNUM:=0) ROWNUMTABLE WHERE CLASSIFICATION='스토어뷰' AND LOGTIME>(NOW() - INTERVAL 1 DAY) GROUP BY STORE_ID) LOGTABLE INNER JOIN STORE ON LOGTABLE.STORE_ID=STORE.STORE_ID) WHERE REVIEWCNT>=0 AND AVERAGELEVEL>=0 ORDER BY CNT DESC) INNERTABLE
WHERE DISTANCE<=20000
) OUTERTABLE WHERE ROWNUM<16
