mapper.select:
SELECT STORE.STORE_ID, ADDRESS, CATEGORY, KAKAOID, TEL, NAME, URL, ROADADDRESS, XLOCATION, YLOCATION, REVIEWCNT, VIEWCNT, TASTELEVEL, PRICELEVEL, SERVICELEVEL, MOODLEVEL, CONVENIENCELEVEL, AVERAGELEVEL, LOGOIMG, ST_DISTANCE_SPHERE(POINT(XLOCATION, YLOCATION), POINT(#{x}, #{y})) "distance", STOREIMG1, (SELECT CONCAT('https://team-moca.s3.ap-northeast-2.amazonaws.com/review/',UU_ID,'_',ORIGINNAME) FROM REVIEWIMAGE WHERE STORE.STORE_ID=REVIEWIMAGE.STORE_ID ORDER BY VIEWS DESC LIMIT 1) AS STOREIMG2 FROM STORE WHERE TAG LIKE CONCAT('%',#{keyword},'%')
AND #{rect[0]}<=XLOCATION AND XLOCATION<=#{rect[2]} AND #{rect[1]}<=YLOCATION AND YLOCATION<=#{rect[3]}
AND ADDRESS LIKE CONCAT('%',#{region},'%')
ORDER BY ${filter}
DESC

  	SELECT rs.UU_ID, rs.ACCOUNT_ID, rs.REVIEW_ID, rs.STORE_ID, rs.PATH, rs.ORIGINNAME, r.WRITEDATE 
	FROM REVIEWIMAGE rs JOIN REVIEW r ON rs.REVIEW_ID = r.REVIEW_ID
	WHERE rs.STORE_ID=#{STORE_ID} order by r.WRITEDATE DESC limit #{LIMIT}

mapper.update:
UPDATE STORE SET tasteLevel=#{tasteLevel}, priceLevel=#{priceLevel}, serviceLevel=#{serviceLevel}, 
moodLevel=#{moodLevel}, convenienceLevel=#{convenienceLevel}, averageLevel=#{averageLevel}
WHERE store_id=#{store_Id}
